import React, { useRef, useEffect } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { Helmet } from "react-helmet-async";
  


const Blog = () => {

// animation functtion
const blogRef = useRef(null);
   

useEffect(() => {
  const anim = gsap.fromTo(
    blogRef.current,
    { y: 100, opacity: 0 },
    {
      y: 0,
      opacity: 1,
      duration: 3,
      ease: 'power2.out',
      scrollTrigger: {
        trigger: blogRef.current,
        start: 'top 80%',
        toggleActions: 'restart none none none', // Re-run every scroll into view
      },
    }
  );

  return () => {
    anim.scrollTrigger?.kill(); // Clean up animation on unmount
  };
}, []);






// card data

const blogPosts = [
    {
      title: "Top 10 Resume Tips to Land Your Dream Job",
      excerpt:
        "A standout resume can make all the difference. Learn the best tips to craft a resume that grabs attention.",
      date: "April 20, 2025",
      author: "Jane Doe",
      image: "data:image/jpeg;base64,"
    },
    {
      title: "How to Ace Your Next Job Interview",
      excerpt:
        "Preparation is key. Here are proven techniques and questions you can expect in your next big interview.",
      date: "May 2, 2025",
      author: "Eliana lee",
      image: "data:image/jpeg;base64,",
    },
    {
      title: "The Future of Remote Work in 2025",
      excerpt:
        "Remote work is evolving fast. Discover the trends shaping how and where we work today.",
      date: "May 5, 2025",
      author: "Sarah Lee",
      image: "data:image/jpeg;base64,",
    },
  ];



  return (
    <div ref={blogRef}>
          <Helmet>
              <title>Blog page</title>
          </Helmet>
  
    <section className="bg-gray-50 py-16 px-6 sm:px-10 md:px-20 lg:px-32">
      <div className="max-w-6xl mx-auto">
        <h2 className="text-4xl font-bold text-blue-600 mb-12 text-center">Latest from Our Blog</h2>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-10">
          {blogPosts.map((post, index) => (
            <div
              key={index}
              className="bg-white rounded-2xl overflow-hidden shadow hover:shadow-lg transition duration-300"
            >
              <img
                src={post.image}
                alt={post.title}
                className="w-full h-48 object-cover"
              />
              <div className="p-6">
                <h3 className="text-xl font-semibold mb-2 text-gray-800 hover:text-blue-600 transition duration-200 cursor-pointer">
                  {post.title}
                </h3>
                <p className="text-gray-600 text-sm mb-4">{post.excerpt}</p>
                <div className="text-sm text-gray-500">
                  <span>{post.date}</span> &middot; <span>{post.author}</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
    </div>
  );
};

export default Blog;